import os
import sys

vel = []
aci = []

def media():
  idx = 0
  vmp = 0
  for i, v in enumerate(vel):
    if v != 0:
      if aci[i] == 'n':
        vmp += v
        idx += 1

  vmp = vmp / idx
  return vmp

while True:
    vel.append(float(input("\nVelocidade (ou 0 para parar): ")))
    if vel[-1] <= 0:
        break
    aci.append((input("Ocorreu um acidente? s/n ")))

print(f"\nA velocidade máxima permitida é: {media():.2f} km/h")

